BEGIN WORK;
DROP TABLE IF EXISTS reviews;


CREATE TABLE reviews(
   movieId    INTEGER  NOT NULL PRIMARY KEY 
  ,metascore  VARCHAR(5)
  ,boxOffice  VARCHAR(15)
  ,website    VARCHAR(44)
  ,imdbRating NUMERIC(3,1)
  ,imdbVotes  VARCHAR(9)
  ,runtime    VARCHAR(7)
  ,language   VARCHAR(33)
  ,rated      VARCHAR(9)
  ,production VARCHAR(33)
  ,released   DATE 
  ,imdbid     VARCHAR(9)
  ,plot       VARCHAR(239)
  ,director   VARCHAR(40)
  ,title      VARCHAR(36) NOT NULL
  ,actors     VARCHAR(78)
  ,response   VARCHAR(4)
  ,type       VARCHAR(5)
  ,awards     VARCHAR(64)
  ,dvd        DATE 
  ,year       INTEGER 
  ,poster     VARCHAR(159)
  ,country    VARCHAR(40)
  ,genre      VARCHAR(44)
  ,writer     VARCHAR(500)
);


COMMIT WORK;